import React, { useState } from "react";
import { useParams } from "react-router-dom";
import data from "../../../db/db.json";
import styles from "./QuestionDetails.module.css";

export default function QuestionDetails() {
  const [checked, setChecked] = useState({});
  const { year, type } = useParams();
  const filteredYear = data.find((yearData) => yearData.year === year);
  const filteredType = filteredYear
    ? filteredYear.data.find((typeData) => typeData.type === type)
    : null;
  const questions = filteredType ? filteredType.data : [];

  const handleChange = (questionId, answerKey) => {
    setChecked((prevState) => ({ ...prevState, [questionId]: answerKey }));
    console.log(checked.questionId);
  };

  const checkAnswer = (question) => {
    const selectedAnswer = checked[question.id];
    return selectedAnswer === question.correctAnswer;
  };
  const getClassName = (question) => {
    if (!checked[question.id]) return "";
    return checkAnswer(question) ? styles.correct : styles.wrong;
  };

  return (
    <ol>
      {questions.map((question) => {
        const answers = question.answers;
        const objectKeys = Object.keys(answers);
        return (
          <li key={question.id}>
            <h3>{question.question}</h3>
            <ol type="A" className={styles.answersList}>
              {objectKeys.map((objectKey, key) => (
                <li key={key}>
                  <label className={styles.answersListItem}>
                    <input
                      checked={checked[question.id] === key}
                      type="checkbox"
                      onChange={() => handleChange(question.id, key)}
                    />
                    <p className={getClassName(question)}>
                      {/* {checked[question.id] &&
                        (checkAnswer(question) ? (
                          <span style={{ color: "green" }}>
                            Răspuns corect!
                          </span>
                        ) : (
                          <span style={{ color: "red" }}>Răspuns greșit!</span>
                        ))} */}
                      {answers[objectKey]}
                    </p>
                  </label>
                </li>
              ))}
            </ol>
          </li>
        );
      })}
    </ol>
  );
}
